import React, { useEffect, useState } from "react";
import { Route, Switch, Link, useParams, useHistory } from "react-router-dom";
import ShowDeck from "./ShowDeck";
import DeckCards from "./DeckCards";
import EditDeck from "../EditDeck/EditDeck";
import EditCard from "../EditCard/EditCard";
import AddCard from "../AddCard/AddCard";
import NavBar from "../NavBar/NavBar";
import { readDeck, listCards } from "../utils/api";


//This function will host:
    //A component rendering the deck and cards (2 separate components)
    //A component rendering the Study Page
    //A componenent rendering the EditDeck Page
    //A component rendering the EditCard Page
    //A component rendering the AddCard Page

export default function HostDeck() {

    //Gather the deckId param
    const { deckId } = useParams()

    //Create initial empty deck 
    const initialDeck = {name: "", description: "", id:{deckId}};

    //Create states
    const [deck, setDeck] = useState(initialDeck);
    const [cards, setCards] = useState(initialCards);

    //useEffect for after initial render to read the deck
    useEffect(() => {

        setDeck(initialDeck);
        setCards(initialCards);

        //Abort Controller
        const abortController = new AbortController();

        //Read the deck
        async function readTheDeck() {
            try{
                const pulledDeck = readDeck(deckId, abortController.signal());
                
                setDeck(pulledDeck);
            } catch (error) {
                if (error=="AbortError") {
                    console.log(error);
                } else {
                    throw error;
                };
            };
        };

        readTheDeck();

        return () => abortController.abort();
    }, []);

    //useEffect for cards that will run after deck has been changed
    useEffect(() => {
        
        //Abort Controller
        const abortController = new AbortController();

        //Gather cards for deck
        async function getTheCards() {
            try{
                const pulledDeck = listCards(deckId, abortController.signal());
                
                setDeck(pulledDeck);
            } catch (error) {
                if (error=="AbortError") {
                    console.log(error);
                } else {
                    throw error;
                };
            };
        };

        getTheCards();

        return () => abortController.abort();
    }, [deck]);

    if(deck) {
        return (
            <>
            <ShowDeck />
            <DeckCards />
            </>
        );
    } else {
        <h3>.....Loading deck and cards</h3>
    }
};
