import React from "react";
import { useParams } from "react-router-dom";
import { updateCard } from "../utils/api/index";
import CardForm from "../AddCard/CardForm";

export default function EditCard({ cards, deckId }) {

    const { cardId } = useParams()

    const card = cards.filter(card => card.id==cardId)

    return (
        <>
            <h2>Edit Card</h2>
            <CardForm deckId={deckId} card={card} cardFunction={updateCard()} />

        </>
    )
}
