import React from "react";
import { useHistory } from "react-router-dom";

export default function StudyCards({ studyCard, setStudyCard, cards, initailCard }) {

    const nextCard = (studyCard, setStudyCard, cards, initialCard) => {
        //Check if studyCard.id < cards length
        if(studyCard.id < cards.length) {
            
        }

    }

    //Iterate through cards
    for (let card in cards) {

        //Check that card id matches with studyCard id
        if(card.id === studyCard.id) {

            //Check studyCard side
            if(studyCard.side==="front") {

                return (
                    <>
                        <p>{card.front}</p>
                        <button onClick={()=> 
                            setStudyCard({...studyCard, [side]: "back"})}>
                                Flip
                        </button>
                    </>
                );
            } else {
                //Side = back

                return (
                    <>
                        <p>{card.back}</p>
                        <button onClick={() =>
                            setStudyCard({...studyCard, [side]: "front"})}>
                                Flip
                        </button>
                        <button onClick={() =>nextCard()}>
                                Next
                        </button>
                    </>
                );
            };
        };
    };
};
