import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { creatCard, updateCard } from "../utils/api/index";

export default function NewCardForm({ deckId, card, cardFunction }) {
    //deck will have either the pre-filled default deck (new) or the current deck info (edit)

    //Grab history for later use
    const history = useHistory();

    //Set new blank deck state
    const [newCard, setNewCard] = useState(card);

    //changeHandler
    const changeHandler = ({ target }) => {
        setNewCard({
            ...newCard,
            [target.name]: target.value
        });
    };

    //submitHandler
    const submitHandler = (newCard, deckId) => {
        //Abort controller for deckFunction API call
        const abortController = new AbortController()

        //use function passed from page
        const submittedCard = cardFunction(deckId, newCard, abortController.signal);
        setNewCard(card);
        history.push(`/decks/${deckId}/cards/new`);

        //Abort controller
        return () => abortController.abort();
    }

    return (
        <>
        <form onSubmit={()=>submitHandler(newCard, deckId)}>
            <label htmlFor="front">
            Front:
            </label>
                <input
                id="front"
                name="front"
                type="textarea"
                onChange={changeHandler}
                value={newCard.front}
                placeholder={card.front} />
            <label htmlFor="back">
            Back:
            </label>
                <input
                id="back"
                name="back"
                type="textarea"
                onChange={changeHandler}
                value={newCard.back}
                placeholder={card.back} />
            <button type="submit">Submit</button>
            <button onClick={() => history.push("/")}>Cancel</button>
        </form>
        </>
    )
};
