import React, { useState, useEffect } from "react";
import { useParams, useRouteMatch, useHistory } from "react-router-dom";
import NotEnoughCards from "./NotEnoughCards";
import StudyCards from "./StudyCards";

export default function StudyDeck({ deck, cards }) {

    //Create initial card state
    const initialCard = {id: 1, side:"front"};

    //Set card state
    const [studyCard, setStudyCard] = useState(initialCard);

    //Set button handlers to be passed to the Cards display

    if (cards.length < 3) {
        return <NotEnoughCards 
        deckName={deck.name} 
        deckId={deck.id} 
        cardsLength={cards.length}/>
     } else {
        return (
            <>
            <h2>Study: {deck.name}</h2>
            <h4>Card {studyCard.id} of {cards.length}</h4>
            <StudyCards 
            studyCard={studyCard}
            setStudyCard={setStudyCard()}
            cards={cards}
            initialCard={initialCard} />
            </>
        )
    }
};
