import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import { listDecks, listCards } from "../utils/api/index";
import DeckDisplay from "./DeckDisplay";
//import "./Home.css";

export default function HomePage() {
    //Initial state for decks
    const initialDecks = [];

    //Set state for decks
    const [decks, setDecks] = useState(initialDecks);

    //Load history
    const history = useHistory();

    //Load the decks and cards to be displayed after initial render
    useEffect(() => {
        //Set decks And Cards to empty values
        setDecks(initialDecks);

        //Abort controller for API fetch
        const abortController = new AbortController();

        //Load the cards and decks
        async function getDecks() {
            try {
                const pulledDecks = await listDecks(abortController.signal)
                setDecks(pulledDecks);
            } catch (error) {
                //Handle the error
                if (error=="AbortError") {
                    console.log("Aborted");
                } else {
                    throw error;
                };
            };
        };

        //Call getDecksAndCards
        getDecks()

        //Abort Controller return
        return () => abortController.abort();
    },[]);

    //Initial cards to reset cards each re-render
    const initialCards = [];

    //State for cards for each deck
    const [cards, setCards] = useState(initialCards);
    
    //useEffect to run each time deckId changes
        //Updates cards state for that deck
    useEffect(() => {

         //Set Cards to empty values
         setCards(initialCards);

         //Abort controller for API fetch
         const abortController = new AbortController();
 
         //Load the cards
         async function getCards(deckId) {
             try {
                 const pulledCards = await listCards(deckId, abortController.signal);
                 setCards(pulledCards);
             } catch (error) {
                 //Handle the error
                 if (error=="AbortError") {
                     console.log("Aborted");
                 } else {
                     throw error;
                 };
             };
         };
 
         //Call getCards
         getCards(deckId)
 
         //Abort Controller return
         return () => abortController.abort();
    }, []);

    //Check to see if decks is empty
    if (decks) {
        return (
            <div>
            <button
            type="button"
            onClick={() => history.push("/decks/new")}>Create Deck</button>
            <hr />
            <DeckDisplay decks={decks} />
            </div>
        )
    } else {
        //No decks
        return <h3>...Loading Decks</h3>
    };
};
