import React, { useState, useEffect } from "react";
import { useParams, useRouteMatch, useHistory } from "react-router-dom";
import NotEnoughCards from "./NotEnoughCards";

export default function StudyDeck({ deck, cards }) {

    //Create initial card state
    const initialCard = {number: 1, side:"front"};

    //Set card state
    const [cardState, setCardState] = useState(initialCard);

    //Set button handlers to be passed to the Cards display

    if (cards.length < 3) {
        return <NotEnoughCards 
        deckName={deck.name} 
        deckId={deck.id} 
        cardsLength={cards.length}/>
    } else {
        return (
            <>
            <h2>Study: {deck.name}</h2>
            <h4>Card {cardState.number} of {cards.length}</h4>
            </>
        )
    }
}
