import React from "react";
import { useHistory } from "react-router-dom";

export default function StudyCards({ studyCard, setStudyCard, cards }) {

    //Iterate through cards
    for (let card in cards) {

        //Check that card id matches with studyCard id
        if(card.id === studyCard.id) {

            //Check studyCard side
            if(studyCard.side==="front") {

                return (
                    <>
                        <p>card.front</p>
                        <button onClick={()=> 
                            setStudyCard({...studyCard, [side]: "back"})}>
                                Flip
                        </button>
                    </>
                )
            }
        }
    }

};
